import React, { useState } from "react";
import "./ExpenseForm.css";

const ExpenseForm = () => {
    // document.getElementById('').addEventListener('click', (event) => {})
    // 자바스크립트에서는 이런식으로 했었음
    
    const [enteredTitle, setEnteredTitle] = useState('');
    //useState 컴포넌트 안에서 선언
    //enteredTitle : 타이틀의 현재 상태를 저장하기 위한 변수 임의로 호출해주지않을거임
    //인풋에 값을 입력을 할때 titleChangedHandler 실행이 되면서 이벤트 값을 가지고
    //안의 내용을 실행할거기때문

    const [enteredAmount, setEnteredAmount] = useState('');
    

    const titleChangedHandler = (event) => {//event라는 prop이 전달됨
        //console.log(event.target.value);//evemt가 가진 타겟 객체의 밸류값을 보자
        
        setEnteredTitle(event.target.value);//setEnteredTitle 호출
        //인풋데이터가 변할때(인풋에 값이 입력되면) enteredTitle에 들어간 데이터가 변하게 설정
    };

    const AmountChangedHndler = (event) => {//event라는 prop이 전달됨
        //console.log(event.target.value);//evemt가 가진 타겟 객체의 밸류값을 보자
        
        setEnteredAmount(event.target.value);//setEnteredTitle 호출
        //인풋데이터가 변할때(인풋에 값이 입력되면) enteredTitle에 들어간 데이터가 변하게 설정
    };

    setEnteredAmount(event.target.value);
    return(
    <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange={titleChangedHandler} />
                {/* 중괄호안에 익명함수를 넣을수도있지만 추천하는 방법은 아님
                여기서 굳이 익명함수를 쓰면 모든 디브들에 전부 익명함수를 붙여넣어야
                하기때문에 함수를 따로 만들고*/}
            </div>{/* 텍스트 입력 받는 부분*/}
            {/* 타이틀이 변할때, 어마운트가변할때, 데이트가 변할때 데이터를 받아서 
            처리할 거기 때문에 onInput이라는 이벤트가 발생하지않음..?
            on은 기본적으로 입력이 된다는 것은 입력란이 변한다는거니까 change를 쓰자*/}

            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01" onChange={AmountChangedHndler}></input>
            </div>{/* 넘버 입력 받는 부분*/}

            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min="2019-01-10" step="2022-12-31" />
            </div>{/* 넘버 입력 받는 부분 / type="date" 자동으로.. 날짜설정해주나?*/}
            </div>
            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        
    </form>
    );
};

export default ExpenseForm;
