import React from "react";
import "./ExpenseForm.css";

const ExpenseForm = () =>{
    return(
    <form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Title</label>
                <input type="text" onChange></input>
            </div>{/* 텍스트 입력 받는 부분*/}
            //타이틀이 변할때, 어마운트가변할때 , 데이트가 변할때 데이터를 받아서 처리할
            거기 때문에 onInput이라는 이벤트가 발생하지않음..?
            on은 기본적으로 입력이 된다는 것은 입력란이 변한다는거니까 change를 쓰자


            <div className="new-expense__control">
                <label>Amount</label>
                <input type="number" min="0.01" step="0.01"></input>
            </div>{/* 넘버 입력 받는 부분*/}

            <div className="new-expense__control">
                <label>Date</label>
                <input type="date" min="2019-01-10" step="2022-12-31"></input>
            </div>{/* 넘버 입력 받는 부분 / type="date" 자동으로.. 날짜설정해주나?*/}

            <div className="new-expense__actions">
                <button type="submit">Add Expense</button>
            </div>
        </div>
    </form>
    );
};

export default ExpenseForm;
