const App = () => {
    const [counter, setCount] = useState(0);
    
    const countHandler = () => {
        setCount(counter + 1)
    };

    return (
      <div>
        <p id="counter">{counter}</p>
        <button onClick={countHandler}>Increment</button>
      </div>
    );
};

export default App;
