  let price = 100;
  const [title, setTitle] = useState("$100");
  
  //함수정의
  const changePricehndler = () => {
    price = 75;
  }
  
  return (
        <div>
            <p>${price}</p>
            <button onClick={changePricehndler}>Apply Discount</button>
        </div>
    );//price를
