import React from 'react';

import './styles.css';


export default function App() {

  const [message, setMessage] = React.useState("")
  const messageHandler = (event) =>{
    setMessage(event.target.value); //gpt에 따르면, 현재 인풋 요소에 입력된 값을 가져와서 
                        //message 상태를 업데이트.message는 항상 최신 입력값을 갖게 됨.
}


    return (
        <form>
            <label>Your message</label>
            <input type="text" onChange={messageHandler}/>  //여기서 받은 메시지를 위에 있는 함수로 보냄 
            <p>{ message.trim().length >= 3 ? "valid message": "invalid message"}</p> //이 p태그 부분이 화면 상에서 유효한 메시지인지 판별하는 부분 
        </form>

     
    );
}
