import React, {useState}  from 'react';
//useState는 hook임

import './styles.css';

// don't change the Component name "App"
// important: In this code editor, use React.useState() instead of just useState()
export default function App() {
  //React.useState();//이게 useState 의 완전한모습임
  const [price, setPrice] = useState(); //컴포넌트 함수안에 useState를 설치해줘야함
  const changePricehndler = () => {//함수정의
   setPrice(75);//const 형태로 price를 저장했기 때문에 직접 값을 넣어줄수없음 오류가남 
  }
  
  return (
        <div>
            <p>${price}</p>
            <button onClick={changePricehndler}>Apply Discount</button>
        </div>
    );//price를
    //on
}
